mapper.insert:
INSERT INTO calendar(calendarKeyNum, calendarStart, calendarEnd, calendarContents, calendarColor, calendarAllDay, calendarAlarm, calendarPhone, calendarRegistrant, calendarRegistrationDate) 
VALUES(nextval(SEQ_Calendar), #{calendarStart}, #{calendarEnd}, #{calendarContents}, #{calendarColor}, ${calendarAllDay}, #{calendarAlarm}, #{calendarPhone}, #{calendarRegistrant}, #{calendarRegistrationDate});


	SELECT lastval(SEQ_Calendar);

mapper.select:
SELECT * FROM calendar
WHERE calendarstart LIKE CONCAT(#{calendarStart},'%')
AND calendarAlarm = #{calendarAlarm}
AND calendarAlarmYn = 1;
