mapper.select:
		SELECT *
    	FROM
    	(
    		SELECT ROW_NUMBER() OVER(ORDER BY ${sidx} ${sord}) RowNo, a.*
    		FROM (
				SELECT *
				FROM category
				WHERE categoryName = #{categoryName}

					AND categoryValue LIKE CONCAT('%',#{categoryValue},'%')

			) a
		) b
    	WHERE b.RowNo between (1+(${page} - 1 ) * ${rows}) and (${page} * ${rows});
