		SELECT DISTINCT issueKeyNum, issueCustomer, issueTitle, issueTester, issueTarget, issueSubTarget, issueFirstWriter, issueDate, issueTosms, issueTosrf, issuePortal, issueJava, issueWas, total, solution, unresolved, hold
    	FROM issue
   		WHERE issueKeyNum = #{issueKeyNum};

		SELECT DISTINCT issueKeyNum, issueCustomer, issueTitle, issueDate, issueTosms, issueTosrf, issuePortal, issueJava, issueWas, total, solution, unresolved, hold
    	FROM issue
   		WHERE issueKeyNum = #{issueKeyNum};

SELECT * FROM issue WHERE issueKeyNum = #{issueKeyNum}
AND (


			OR issueApplyYn = #{arr}


)

SELECT MAX(issueSortNum) 
FROM issue 
WHERE issueSortNum < ${issueSortNum}
AND issueKeyNum = ${issueKeyNum};

SELECT issueSortNum, issuePrimaryKeyNum
FROM issue 
WHERE issueSortNum > ${issueSortNum}
AND issueKeyNum = ${issueKeyNum}
ORDER BY issueSortNum
LIMIT 1;
