mapper.select:
		SELECT *
    	FROM
    	(
    		SELECT ROW_NUMBER() OVER(ORDER BY ${sidx} ${sord}) RowNo, a.*
    		FROM (
				SELECT *
				FROM menuSetting
				WHERE menuType = 'sub'
				AND menuParentKeyNum = #{menuParentKeyNum}
			) a
		) b
    	WHERE b.RowNo between (1+(${page} - 1 ) * ${rows}) and (${page} * ${rows});

		SELECT *
    	FROM
    	(
    		SELECT ROW_NUMBER() OVER(ORDER BY ${sidx} ${sord}) RowNo, a.*
    		FROM (
				SELECT *
				FROM menuSetting
				WHERE menuType = 'item'
				AND menuParentKeyNum = #{menuParentKeyNum}
			) a
		) b
    	WHERE b.RowNo between (1+(${page} - 1 ) * ${rows}) and (${page} * ${rows});

SELECT m.*, p.*
FROM menusetting m, ${menuTitle}_${menuKeyNum} p
WHERE m.menuParentKeyNum = ${menuKeyNum}
AND m.menuTitle IN (
    SELECT COLUMN_NAME
    FROM INFORMATION_SCHEMA.COLUMNS
    WHERE TABLE_NAME = '${menuTitle}_${menuKeyNum}'
)
AND p.productVersionKeyNum = ${productVersionKeyNum}
ORDER BY m.menuSort ASC;
