mapper.update:
CREATE TABLE ${tableName} (
	productVersionKeyNum INT AUTO_INCREMENT PRIMARY KEY,
	menuKeyNum INT DEFAULT ${menuParentKeyNum},
	${menuTitle} ${menuItemType}
)

mapper.select:
		SELECT *
    	FROM
    	(
    		SELECT ROW_NUMBER() OVER(ORDER BY ${sidx} ${sord}) RowNo, a.*
    		FROM (
				SELECT *
				FROM ${productData}

			) a
		) b
    	WHERE b.RowNo between (1+(${page} - 1 ) * ${rows}) and (${page} * ${rows});

SELECT * FROM ( SELECT ROW_NUMBER() OVER(ORDER BY ${sidx} ${sord}) RowNo, a.* FROM ( SELECT pv.productVersionKeyNum, main.menuTitle AS mainMenuTitle, sub.menuTitle AS subMenuTitle, pv.packageName, pv.location, pv.version FROM (
SELECT productVersionKeyNum, menuKeyNum, packageName, location, '${table.substring(table.indexOf("_") + 1)}' AS version FROM ${table}
AND packageName LIKE CONCAT('%',#{packageNameView},'%')
AND location LIKE CONCAT('%',#{locationView},'%')
) pv LEFT JOIN menusetting sub ON pv.menuKeyNum = sub.menuKeyNum AND sub.menuType = 'sub'
AND sub.menuTitle LIKE CONCAT('%',#{subTitleView},'%')
LEFT JOIN menusetting main ON ( sub.menuParentKeyNum = main.menuKeyNum AND main.menuType = 'main' )
WHERE main.menuTitle LIKE CONCAT('%',#{mainTitleView},'%')
OR ( pv.menuKeyNum = main.menuKeyNum AND main.menuType = 'main' AND sub.menuKeyNum IS NULL ) ) a ) b WHERE b.RowNo between (1+(${page} - 1 ) * ${rows}) and (${page} * ${rows});

		SELECT table_name
		FROM information_schema.tables
		WHERE table_schema = #{databaseName}
  			AND table_name LIKE 'productversion_%';

SELECT COUNT(*) FROM (
SELECT productVersionKeyNum, menuKeyNum, packageName, location, '${table.substring(table.indexOf("_") + 1)}' AS version FROM ${table}
) pv LEFT JOIN menusetting sub ON pv.menuKeyNum = sub.menuKeyNum AND sub.menuType = 'sub' LEFT JOIN menusetting main ON ( sub.menuParentKeyNum = main.menuKeyNum AND main.menuType = 'main' ) OR ( pv.menuKeyNum = main.menuKeyNum AND main.menuType = 'main' AND sub.menuKeyNum IS NULL )
